<?phpclass User extends UserAppModel {    var $name = 'User';    public $actsAs = array('Containable', 'Search.Searchable');    public $belongsTo = array('AccessRightCategory' => array('className' => 'AccessRightCategory', 'foreignKey' => 'access_right_category_id'));    public $filterArgs = array(        array('name' => 'access_right_category_id', 'type' => 'query', 'method' => 'rightcategoryfilterQuery'),        array('name' => 'email', 'type' => 'query', 'method' => 'emailfilterQuery'),        array('name' => 'firstname', 'type' => 'query', 'method' => 'firstnamelfilterQuery'),    );    public function rightcategoryfilterQuery($data = array()) {        if (!isset($data['access_right_category_id'])) {            return array();        }        $query = $data['access_right_category_id'];        if ($query) {            return array(                'OR' => array(                    'User.access_right_category_id' => $query                )            );        } else {            return array();        }    }    public function usernamefilterQuery($data = array()) {        if (!isset($data['username'])) {            return array();        }        $query = $data['username'];        if ($query) {            return array(                'OR' => array(                    'User.username LIKE ' => "%" . $query . "%"                )            );        } else {            return array();        }    }       public function firstnamelfilterQuery($data = array()) {        if (!isset($data['firstname'])) {            return array();        }        $query = $data['firstname'];        if ($query) {            return array(                'OR' => array(                    'User.firstname LIKE ' => "%" . $query . "%"                )            );        } else {            return array();        }    }    public function emailfilterQuery($data = array()) {        if (!isset($data['email'])) {            return array();        }        $query = $data['email'];        if ($query) {            return array(                'OR' => array(                    'User.email LIKE ' => "%" . $query . "%"                )            );        } else {            return array();        }    }    function CheckValidUser() {        $validate1 = array(                        'mobile' => array(                'valid' => array(                    'rule' => 'notEmpty',                    'required' => true,                    'allowEmpty' => false,                    'message' => 'Please enter Mobile'                ),                'mustUnique' => array(                    'rule' => 'isUnique',                    //'on'=>'create',                    'message' => 'This Mobile is already exist',                ),            ),            'email' => array(                'valid' => array(                    'rule' => 'notEmpty',                    'required' => true,                    'allowEmpty' => false,                    'message' => 'Please enter Email'                ),                'mustBeEmail' => array(                    'rule' => array('email'),                    'message' => 'Please enter valid email',                    'last' => true                ),                'mustUnique' => array(                    'rule' => 'isUnique',                    //'on'=>'create',                    'message' => 'This Email is already exist',                ),            )        );        $this->validate = $validate1;        return $this->validates();    }}?>